import { Component, Input, Output, EventEmitter } from '@angular/core';
import { CommonModule } from '@angular/common';
import { IconFontComponent } from '../icon-font/icon-font.component';

export type ButtonSize = 'small' | 'medium' | 'large';
export type ButtonVariant = 'filled' | 'outlined' | 'flat' | 'link';
export type ButtonState = 'default' | 'hovered' | 'active' | 'disabled' | 'focused';

/**
 * Button Component - Bioma 2.0 Design System
 *
 * Bot贸n configurable con m煤ltiples variantes, tama帽os y estados.
 * Soporta 铆conos al inicio y final, y todos los estados interactivos.
 *
 * @example
 * ```html
 * <!-- Button b谩sico -->
 * <bio-button label="Click me"></bio-button>
 *
 * <!-- Button con 铆cono -->
 * <bio-button
 *   label="Agregar"
 *   [iconStart]="true"
 *   (click)="handleClick()">
 * </bio-button>
 *
 * <!-- Button outlined small -->
 * <bio-button
 *   label="Cancelar"
 *   variant="outlined"
 *   size="small">
 * </bio-button>
 * ```
 */
@Component({
  selector: 'bio-button',
  standalone: true,
  imports: [CommonModule, IconFontComponent],
  templateUrl: './button.component.html',
  styleUrls: ['./button.component.scss']
})
export class ButtonComponent {
  /**
   * Texto del bot贸n
   */
  @Input() label: string = 'Button';

  /**
   * Tama帽o del bot贸n
   * - small: 40px altura
   * - medium: 48px altura (default)
   * - large: 56px altura
   */
  @Input() size: ButtonSize = 'large';

  /**
   * Variante visual del bot贸n
   * - filled: Fondo s贸lido de marca (default)
   * - outlined: Solo borde
   * - flat: Sin borde
   * - link: Estilo de enlace
   */
  @Input() variant: ButtonVariant = 'filled';

  /**
   * Estado del bot贸n (normalmente manejado autom谩ticamente)
   */
  @Input() state: ButtonState = 'default';

  /**
   * Muestra 铆cono al inicio del bot贸n
   */
  @Input() iconStart: boolean = false;

  /**
   * Muestra 铆cono al final del bot贸n
   */
  @Input() iconEnd: boolean = false;

  /**
   * Nombre del 铆cono Material Symbols para iconStart
   */
  @Input() iconStartName: string = 'add';

  /**
   * Nombre del 铆cono Material Symbols para iconEnd
   */
  @Input() iconEndName: string = 'keyboard_arrow_down';

  /**
   * Deshabilita el bot贸n
   */
  @Input() disabled: boolean = false;

  /**
   * Bot贸n ocupa todo el ancho disponible
   */
  @Input() fullWidth: boolean = false;

  /**
   * Tipo de bot贸n HTML
   */
  @Input() type: 'button' | 'submit' | 'reset' = 'button';

  /**
   * Evento de click
   */
  @Output() clicked = new EventEmitter<MouseEvent>();

  /**
   * Clases CSS din谩micas basadas en props.
   * El estado se maneja via CSS (:hover, :active, :focus-visible) excepto
   * cuando se pasa expl铆citamente para Storybook/demos.
   */
  get buttonClasses(): string[] {
    const classes = [
      'bio-button',
      `bio-button--${this.size}`,
      `bio-button--${this.variant}`,
      this.fullWidth ? 'bio-button--full-width' : '',
      this.disabled ? 'bio-button--disabled' : ''
    ];

    // Estado forzado para Storybook (cuando se pasa expl铆citamente)
    if (this.state && this.state !== 'default') {
      classes.push(`bio-button--${this.state}`);
    }

    return classes.filter(Boolean);
  }

  /**
   * Manejador de click
   */
  handleClick(event: MouseEvent): void {
    if (!this.disabled) {
      this.clicked.emit(event);
    }
  }
}

